definition.script:
pipeline {
    agent any

    stages {

        stage("Download") {

            steps {

                sh 'rm -r -f /project'
                sh 'git clone https://github.com/nicolasances/${totoMSname}.git /project'
            }
        }

        stage("Compile") {

            steps {

                dir('/project/${totoMSname}') {
                    sh 'mvn clean install -e'
                }
            }
        }

        stage("Build Docker Image") {

            steps {  
                sh 'cp /project/${totoMSname}/target/*.jar /project/${totoMSname}'
                dir('/project/${totoMSname}') {

                    sh 'docker build -t ${totoMSname} .'
                }
            }
        }

        stage("Deploy") {

            steps {

                sh 'docker stop ${totoMSname} || true'
                sh 'docker rm ${totoMSname} || true'
                sh 'docker run -d --network totonet --name ${totoMSname} ${totoMSname}'
            }
        }
    }
}
